mapper.delete:
delete from store_commodity_type 	 
where ID IN
 #{item}

mapper.select:
SELECT COUNT(*) from store_commodity_type _tbl



		 and NAME=#{name}  	


		 and RECORD_SORT=#{recordSort}  	


		 and PIC_PATH=#{picPath}  	


		 and CREATE_ID=#{createId}  	


		 and CREATE_TIME=#{createTime}  	


		 and DELETE_ID=#{deleteId}  	


		 and DELETE_TIME=#{deleteTime}  	


		 and RECORD_STATE=#{recordState}

select  	
_tbl.ID id,	
_tbl.NAME name,	
_tbl.RECORD_SORT recordSort,	
_tbl.PIC_PATH picPath,	
_tbl.CREATE_ID createId,	
_tbl.CREATE_TIME createTime,	
_tbl.DELETE_ID deleteId,	
_tbl.DELETE_TIME deleteTime,	
_tbl.RECORD_STATE recordState	
from store_commodity_type _tbl	



		 and NAME=#{name}  	


		 and RECORD_SORT=#{recordSort}  	


		 and PIC_PATH=#{picPath}  	


		 and CREATE_ID=#{createId}  	


		 and CREATE_TIME=#{createTime}  	


		 and DELETE_ID=#{deleteId}  	


		 and DELETE_TIME=#{deleteTime}  	


		 and RECORD_STATE=#{recordState}

select  	
_tbl.ID id,	
_tbl.NAME name,	
_tbl.RECORD_SORT recordSort,	
_tbl.PIC_PATH picPath,	
_tbl.CREATE_ID createId,	
_tbl.CREATE_TIME createTime,	
_tbl.DELETE_ID deleteId,	
_tbl.DELETE_TIME deleteTime,	
_tbl.RECORD_STATE recordState	
from store_commodity_type _tbl	



		 and NAME=#{name}  	


		 and RECORD_SORT=#{recordSort}  	


		 and PIC_PATH=#{picPath}  	


		 and CREATE_ID=#{createId}  	


		 and CREATE_TIME=#{createTime}  	


		 and DELETE_ID=#{deleteId}  	


		 and DELETE_TIME=#{deleteTime}  	


		 and RECORD_STATE=#{recordState}  	



limit 1
